import { Component, OnInit } from '@angular/core';
import { Router } from '@angular/router';

interface Document {
  code: string;
  title: string;
  location: string;
  content: string[];
  date?: string;
  saved?: boolean;
}

@Component({
  selector: 'app-dashboard',
  templateUrl: './dashboard.component.html',
  styleUrls: ['./dashboard.component.css']
})
export class DashboardComponent implements OnInit {
  documents: Document[] = [
    {
      code: '47-0412',
      title: 'FÉ É A SUBSTÂNCIA',
      location: 'Oakland Califórnia E.U.A.',
      content: ['Estamos recebendo alguns novos dispositivos para gravação...'],
      date: '12/04/1947',
      saved: false
    },
    {
      code: '48-0501',
      title: 'A RESSURREIÇÃO',
      location: 'Los Angeles Califórnia E.U.A.',
      content: ['Nesta noite especial, vamos falar sobre um tema muito importante...'],
      date: '01/05/1948',
      saved: true
    },
    {
      code: '49-0718',
      title: 'O ANJO DE DEUS',
      location: 'São Paulo Brasil',
      content: ['É uma grande alegria estar aqui no Brasil pela primeira vez...'],
      date: '18/07/1949',
      saved: false
    }
  ];

  savedDocuments: Document[] = [];
  isDarkMode: boolean = false;
  showReadingView: boolean = false;
  currentDocument: Document | null = null;

  constructor(private router: Router) {}

  ngOnInit() {
    this.isDarkMode = localStorage.getItem('theme') === 'dark';
    this.applyTheme();
    this.savedDocuments = this.documents.filter(doc => doc.saved);
  }

  toggleSave(document: Document) {
    document.saved = !document.saved;
    this.savedDocuments = this.documents.filter(doc => doc.saved);
  }

  openDocument(document: Document) {
    this.currentDocument = document;
    this.showReadingView = true;
  }

  closeReadingView() {
    this.showReadingView = false;
    this.currentDocument = null;
  }

  logout() {
    // Aqui você pode adicionar a lógica de logout
    this.router.navigate(['/']);
  }

  toggleDarkMode() {
    this.isDarkMode = !this.isDarkMode;
    this.applyTheme();
    localStorage.setItem('theme', this.isDarkMode ? 'dark' : 'light');
  }

  private applyTheme() {
    if (this.isDarkMode) {
      document.documentElement.classList.add('dark');
    } else {
      document.documentElement.classList.remove('dark');
    }
  }
}
