import { Component, OnInit } from '@angular/core';
import { Router } from '@angular/router';

interface Message {
  id: string;
  title: string;
  preview: string;
  content: string[];
  date: Date;
  location: string;
  bookmarked: boolean;
  code: string;
}

@Component({
  selector: 'app-dashboard',
  templateUrl: './dashboard.component.html',
  styleUrls: ['./dashboard.component.css']
})
export class DashboardComponent implements OnInit {
  isDarkMode: boolean = false;
  recentMessages: Message[] = [
    {
      id: '1',
      title: 'FÉ É A SUBSTÂNCIA',
      preview: 'Estamos recebendo alguns novos dispositivos para gravação...',
      content: [
        'Estamos recebendo alguns novos dispositivos para gravação.',
        'Mal sabemos cada noite onde vamos estar...'
      ],
      date: new Date('2024-02-08'),
      location: 'Oakland Califórnia E.U.A.',
      bookmarked: false,
      code: '47-0412'
    },
    {
      id: '2',
      title: 'A RESSURREIÇÃO',
      preview: 'Nesta noite especial, vamos falar sobre um tema muito importante...',
      content: [
        'Nesta noite especial, vamos falar sobre um tema muito importante.',
        'A ressurreição é o fundamento da nossa fé...'
      ],
      date: new Date('2024-02-07'),
      location: 'Los Angeles Califórnia E.U.A.',
      bookmarked: true,
      code: '48-0501'
    },
    {
      id: '3',
      title: 'O ANJO DE DEUS',
      preview: 'É uma grande alegria estar aqui no Brasil pela primeira vez...',
      content: [
        'É uma grande alegria estar aqui no Brasil pela primeira vez.',
        'Muitos têm me perguntado sobre o Anjo do Senhor...'
      ],
      date: new Date('2024-02-06'),
      location: 'São Paulo Brasil',
      bookmarked: false,
      code: '49-0718'
    }
  ];

  savedMessages: Message[] = [];

  constructor(private router: Router) {
    this.savedMessages = this.recentMessages.filter(m => m.bookmarked);
  }

  ngOnInit() {
    const savedTheme = localStorage.getItem('theme');
    this.isDarkMode = savedTheme === 'dark';
    this.applyTheme();
  }

  toggleDarkMode(): void {
    this.isDarkMode = !this.isDarkMode;
    this.applyTheme();
    localStorage.setItem('theme', this.isDarkMode ? 'dark' : 'light');
  }

  private applyTheme(): void {
    if (this.isDarkMode) {
      document.documentElement.classList.add('dark');
    } else {
      document.documentElement.classList.remove('dark');
    }
  }

  toggleBookmark(message: Message, event: Event): void {
    event.stopPropagation();
    message.bookmarked = !message.bookmarked;

    if (message.bookmarked) {
      this.savedMessages.push(message);
    } else {
      this.savedMessages = this.savedMessages.filter(m => m.id !== message.id);
    }
  }

  removeFromSaved(message: Message): void {
    message.bookmarked = false;
    this.savedMessages = this.savedMessages.filter(m => m.id !== message.id);
  }

  openMessage(message: Message): void {
    // Aqui você pode implementar a navegação para a tela de leitura
    // Por exemplo:
    // this.router.navigate(['/read', message.id]);
    console.log('Abrindo mensagem:', message);
  }

  logout(): void {
    // Implementar lógica de logout
    this.router.navigate(['/']);
  }
}
