import { Component, OnInit } from '@angular/core';

interface Document {
  code: string;
  title: string;
  location: string;
  content: string[];
}

@Component({
  selector: 'app-home',
  templateUrl: './home.component.html',
  styleUrls: ['./home.component.css']
})
export class HomeComponent implements OnInit {
  title = 'alarido-projeto-angular';
  searchQuery: string = '';
  searchResults: number = 0;
  showSearchResults: boolean = false;
  showBooksDropdown: boolean = false;
  showLanguageDropdown: boolean = false;
  showLoginModal: boolean = false;
  isDarkMode: boolean = false;
  filteredDocuments: Document[] = [];

  documents: Document[] = [
    {
      code: "DISC001",
      title: "Discurso de Posse",
      location: "Brasília, DF",
      content: [
        "Excelentíssimos senhores e senhoras, é com grande honra que me dirijo a vocês neste momento solene...",
        "Nossa nação enfrenta desafios significativos, mas juntos podemos superá-los...",
        "Comprometo-me a trabalhar incansavelmente pelo bem-estar de todos os cidadãos..."
      ]
    },
    {
      code: "DISC002",
      title: "Pronunciamento à Nação",
      location: "Palácio do Planalto",
      content: [
        "Brasileiras e brasileiros, venho a público para comunicar importantes medidas...",
        "As reformas propostas visam fortalecer nossa economia e criar oportunidades...",
        "Conto com o apoio de todos para construirmos um país mais justo e próspero..."
      ]
    },
    {
      code: "DISC003",
      title: "Abertura do Ano Legislativo",
      location: "Congresso Nacional",
      content: [
        "Senhores parlamentares, iniciamos hoje mais um ano de trabalhos legislativos...",
        "Nossa agenda legislativa é ambiciosa e focada no desenvolvimento nacional...",
        "A cooperação entre os poderes é fundamental para o progresso do Brasil..."
      ]
    }
  ];

  currentDocument: Document = this.documents[0];

  constructor() { }

  ngOnInit(): void {
    const savedTheme = localStorage.getItem('theme');
    this.isDarkMode = savedTheme === 'dark';
    this.filteredDocuments = this.documents;
  }

  searchDocuments() {
    if (!this.searchQuery) {
      this.filteredDocuments = this.documents;
    } else {
      this.filteredDocuments = this.documents.filter(doc =>
        doc.title.toLowerCase().includes(this.searchQuery.toLowerCase()) ||
        doc.content.some(text => text.toLowerCase().includes(this.searchQuery.toLowerCase()))
      );
    }
    this.searchResults = this.filteredDocuments.length;
    this.showSearchResults = true;
  }

  onSearchInput() {
    if (!this.searchQuery) {
      this.showSearchResults = false;
      this.searchResults = 0;
    } else {
      this.searchDocuments();
    }
  }

  selectSearchResult(doc: Document) {
    this.currentDocument = doc;
    this.showSearchResults = false;
    this.searchQuery = '';
  }

  selectBook(doc: Document) {
    this.currentDocument = doc;
    this.showBooksDropdown = false;
  }

  toggleBooksDropdown() {
    this.showBooksDropdown = !this.showBooksDropdown;
    this.showLanguageDropdown = false;
  }

  toggleLanguageDropdown() {
    this.showLanguageDropdown = !this.showLanguageDropdown;
    this.showBooksDropdown = false;
  }

  toggleLoginModal() {
    this.showLoginModal = !this.showLoginModal;
  }

  toggleDarkMode() {
    this.isDarkMode = !this.isDarkMode;
    localStorage.setItem('theme', this.isDarkMode ? 'dark' : 'light');
  }
}
